import React, { useState } from 'react';
import jsPDF from 'jspdf';

const Home = () => {
  const [isDownloading, setIsDownloading] = useState(false);

  const generateCV = () => {
    setIsDownloading(true);

    // Create new PDF document
    const doc = new jsPDF('p', 'mm', 'a4');
    const pageWidth = doc.internal.pageSize.getWidth();
    const pageHeight = doc.internal.pageSize.getHeight();

    // Colors
    const primaryColor = [59, 130, 246]; // Blue
    const secondaryColor = [147, 51, 234]; // Purple
    const textColor = [31, 41, 55]; // Dark gray
    const lightGray = [156, 163, 175]; // Light gray

    // Helper function to add text with styling
    const addText = (text, x, y, options = {}) => {
      const { fontSize = 12, color = textColor, font = 'helvetica', align = 'left', maxWidth = null } = options;
      doc.setFontSize(fontSize);
      doc.setTextColor(...color);
      doc.setFont(font, options.bold ? 'bold' : 'normal');

      if (maxWidth) {
        const lines = doc.splitTextToSize(text, maxWidth);
        doc.text(lines, x, y);
        return y + (lines.length * fontSize * 0.4);
      } else {
        doc.text(text, x, y, { align });
        return y + fontSize * 0.4;
      }
    };

    // Helper function to add line
    const addLine = (x1, y1, x2, y2, color = primaryColor, width = 0.5) => {
      doc.setDrawColor(...color);
      doc.setLineWidth(width);
      doc.line(x1, y1, x2, y2);
    };

    // Header Section
    doc.setFillColor(...primaryColor);
    doc.rect(0, 0, pageWidth, 35, 'F');

    // Name
    addText('SURYANSH SRIVASTAVA', pageWidth / 2, 15, {
      fontSize: 24,
      color: [255, 255, 255],
      font: 'helvetica',
      bold: true,
      align: 'center'
    });

    // Title
    addText('Full Stack Developer & UI/UX Designer', pageWidth / 2, 22, {
      fontSize: 14,
      color: [255, 255, 255],
      font: 'helvetica',
      align: 'center'
    });

    // Contact Information
    let currentY = 45;
    const contactInfo = [
      { icon: 'ðŸ“§', text: 'suryanshsri10@gmail.com' },
      { icon: 'ðŸ“±', text: '+91 8318202728' },
      { icon: 'ðŸ’¼', text: 'linkedin.com/in/suryansh-srivastava10' },
      { icon: 'ðŸ’»', text: 'github.com/SuryanshSrivastava-10' }
    ];

    contactInfo.forEach((contact, index) => {
      const x = 20 + (index * 45);
      addText(contact.icon, x, currentY, { fontSize: 12 });
      addText(contact.text, x + 8, currentY, { fontSize: 10, color: lightGray });
    });

    currentY += 15;

    // Professional Summary
    addText('PROFESSIONAL SUMMARY', 20, currentY, {
      fontSize: 16,
      color: primaryColor,
      bold: true
    });
    currentY += 8;

    addLine(20, currentY, pageWidth - 20, currentY, primaryColor, 1);
    currentY += 8;

    const summary = "Passionate Full Stack Developer and UI/UX Designer with expertise in creating beautiful, functional, and user-friendly digital experiences. Skilled in modern web technologies and committed to turning complex problems into simple, elegant solutions.";

    currentY = addText(summary, 20, currentY, {
      fontSize: 11,
      color: textColor,
      maxWidth: pageWidth - 40
    }) + 5;

    // Technical Skills
    addText('TECHNICAL SKILLS', 20, currentY, {
      fontSize: 16,
      color: primaryColor,
      bold: true
    });
    currentY += 8;

    addLine(20, currentY, pageWidth - 20, currentY, primaryColor, 1);
    currentY += 8;

    const skills = [
      'Frontend: React.js, HTML5, CSS3, JavaScript, Tailwind CSS',
      'Backend: Node.js, Python, Django, Express.js',
      'Database: MongoDB, SQL, MySQL',
      'Tools: Git, VS Code, Figma, Adobe Creative Suite',
      'Languages: JavaScript, Python, PHP, C++, C'
    ];

    skills.forEach(skill => {
      addText('â€¢ ' + skill, 20, currentY, { fontSize: 11, color: textColor });
      currentY += 6;
    });

    currentY += 5;

    // Projects
    addText('PROJECTS', 20, currentY, {
      fontSize: 16,
      color: primaryColor,
      bold: true
    });
    currentY += 8;

    addLine(20, currentY, pageWidth - 20, currentY, primaryColor, 1);
    currentY += 8;

    const projects = [
      {
        title: 'Comprehensive Blog Management System',
        duration: 'Oct 2024 â€“ Dec 2024',
        tech: 'React.js, Node.js, MongoDB',
        description: 'Developed a full-stack blog platform with user management, post creation/editing, and content organization features.'
      },
      {
        title: 'Scholarship Redressal Portal',
        duration: 'Aug 2023 â€“ Sept 2023',
        tech: 'HTML, CSS, JavaScript, SQL, Django, Python',
        description: 'Built a portal for students to track scholarship status and disbursement independently.'
      },
      {
        title: 'Online IDE Platform',
        duration: 'Nov 2022 â€“ Dec 2022',
        tech: 'HTML, CSS, JavaScript, PHP',
        description: 'Created an online coding environment supporting multiple programming languages with syntax highlighting.'
      }
    ];

    projects.forEach(project => {
      // Project Title
      addText(project.title, 20, currentY, {
        fontSize: 12,
        color: textColor,
        bold: true
      });
      currentY += 6;

      // Duration and Tech
      addText(`${project.duration} | ${project.tech}`, 20, currentY, {
        fontSize: 10,
        color: secondaryColor,
        bold: true
      });
      currentY += 5;

      // Description
      currentY = addText(project.description, 20, currentY, {
        fontSize: 10,
        color: textColor,
        maxWidth: pageWidth - 40
      }) + 8;
    });

    // Education
    addText('EDUCATION', 20, currentY, {
      fontSize: 16,
      color: primaryColor,
      bold: true
    });
    currentY += 8;

    addLine(20, currentY, pageWidth - 20, currentY, primaryColor, 1);
    currentY += 8;

    addText('Bachelor of Technology in Computer Science', 20, currentY, {
      fontSize: 12,
      color: textColor,
      bold: true
    });
    currentY += 5;

    addText('University Name | 2020 - 2024', 20, currentY, {
      fontSize: 10,
      color: secondaryColor
    });
    currentY += 8;

    // Footer
    doc.setFillColor(...primaryColor);
    doc.rect(0, pageHeight - 10, pageWidth, 10, 'F');

    addText('Â© 2024 Suryansh Srivastava. All rights reserved.', pageWidth / 2, pageHeight - 5, {
      fontSize: 8,
      color: [255, 255, 255],
      align: 'center'
    });

    // Save the PDF
    doc.save('Suryansh_Srivastava_CV.pdf');

    setTimeout(() => {
      setIsDownloading(false);
    }, 1000);
  };

  return (
    <section id="home" className="min-h-screen flex items-center justify-center gradient-bg relative overflow-hidden">
      {/* Space-themed Background Elements */}
      <div className="absolute inset-0 pointer-events-none">
        {/* Floating Stars */}
        <div className="absolute top-20 left-10 text-space-blue-300 opacity-60 animate-bounce-slow">
          <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
          </svg>
        </div>
        <div className="absolute top-32 right-16 text-cosmic-purple-300 opacity-50 animate-bounce-slow" style={{animationDelay: '1s'}}>
          <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
          </svg>
        </div>
        <div className="absolute bottom-32 left-20 text-space-blue-400 opacity-40 animate-bounce-slow" style={{animationDelay: '2s'}}>
          <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 24 24">
            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
          </svg>
        </div>
        <div className="absolute bottom-20 right-10 text-cosmic-purple-400 opacity-45 animate-bounce-slow" style={{animationDelay: '0.5s'}}>
          <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
          </svg>
        </div>

        {/* Additional Stars */}
        <div className="absolute top-40 left-1/4 text-space-blue-200 opacity-30">
          <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 24 24">
            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
          </svg>
        </div>
        <div className="absolute bottom-40 right-1/4 text-cosmic-purple-200 opacity-35">
          <svg className="w-2 h-2" fill="currentColor" viewBox="0 0 24 24">
            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
          </svg>
        </div>

        {/* Constellation Lines */}
        <div className="absolute top-1/4 left-1/3 w-16 h-px bg-gradient-to-r from-space-blue-400/30 to-transparent"></div>
        <div className="absolute bottom-1/3 right-1/3 w-12 h-px bg-gradient-to-r from-cosmic-purple-400/30 to-transparent"></div>
      </div>

      <div className="container-max section-padding relative z-10">
        <div className="grid lg:grid-cols-2 gap-12 items-center">
          {/* Left Side - Animated Text */}
          <div className="text-center lg:text-left order-2 lg:order-1">
            <div className="space-y-6">
              {/* Greeting */}
              <div className="animate-fade-in">
                <p className="text-lg sm:text-xl text-space-blue-300 font-medium mb-2">
                  Hello, I'm
                </p>
              </div>

              {/* Animated Name */}
              <div className="animate-slide-up">
                <h1 className="text-4xl sm:text-5xl lg:text-6xl xl:text-7xl font-bold text-white mb-4">
                  <span className="inline-block animate-pulse">Suryansh</span>
                  <br />
                  <span className="inline-block text-gradient animate-pulse" style={{animationDelay: '0.5s'}}>
                    Srivastava
                  </span>
                </h1>
              </div>

              {/* Animated Title */}
              <div className="animate-slide-up" style={{animationDelay: '0.3s'}}>
                <h2 className="text-xl sm:text-2xl lg:text-3xl font-semibold text-space-dark-200 mb-6">
                  <span className="inline-block animate-bounce-slow">Full Stack Developer</span>
                  <br />
                  <span className="inline-block animate-bounce-slow" style={{animationDelay: '0.2s'}}>
                    & UI/UX Designer
                  </span>
                </h2>
              </div>

              {/* Description */}
              <div className="animate-slide-up" style={{animationDelay: '0.6s'}}>
                <p className="text-lg sm:text-xl text-space-dark-300 leading-relaxed mb-8 max-w-2xl mx-auto lg:mx-0">
                  Passionate about creating beautiful, functional, and user-friendly digital experiences. 
                  I love turning complex problems into simple, elegant solutions.
                </p>
              </div>

              {/* CTA Buttons */}
              <div className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start animate-slide-up" style={{animationDelay: '0.9s'}}>
                <button className="btn-primary text-lg px-8 py-4 hover:scale-105 transition-transform duration-300">
                  View My Work
                </button>
                <button className="btn-secondary text-lg px-8 py-4 hover:scale-105 transition-transform duration-300">
                  Download CV
                </button>
              </div>

              {/* Social Links */}
              <div className="flex justify-center lg:justify-start space-x-6 mt-12 animate-slide-up" style={{animationDelay: '1.2s'}}>
                <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer" className="text-space-dark-300 hover:text-space-blue-400 transition-all duration-300 hover:scale-110">
                  <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
                  </svg>
                </a>
                <a href="https://pinterest.com" target="_blank" rel="noopener noreferrer" className="text-space-dark-300 hover:text-space-blue-400 transition-all duration-300 hover:scale-110">
                  <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M12.017 0C5.396 0 .029 5.367.029 11.987c0 5.079 3.158 9.417 7.618 11.174-.105-.949-.199-2.403.041-3.439.219-.937 1.406-5.957 1.406-5.957s-.359-.72-.359-1.781c0-1.663.967-2.911 2.168-2.911 1.024 0 1.518.769 1.518 1.688 0 1.029-.653 2.567-.992 3.992-.285 1.193.6 2.165 1.775 2.165 2.128 0 3.768-2.245 3.768-5.487 0-2.861-2.063-4.869-5.008-4.869-3.41 0-5.409 2.562-5.409 5.199 0 1.033.394 2.143.889 2.741.099.12.112.225.085.345-.09.375-.293 1.199-.334 1.363-.053.225-.172.271-.402.165-1.495-.69-2.433-2.878-2.433-4.646 0-3.776 2.748-7.252 7.92-7.252 4.158 0 7.392 2.967 7.392 6.923 0 4.135-2.607 7.462-6.233 7.462-1.214 0-2.357-.629-2.746-1.378l-.748 2.853c-.271 1.043-1.002 2.35-1.492 3.146C9.57 23.812 10.763 24.009 12.017 24.009c6.624 0 11.99-5.367 11.99-11.988C24.007 5.367 18.641.001 12.017.001z"/>
                  </svg>
                </a>
                <a href="https://github.com" target="_blank" rel="noopener noreferrer" className="text-space-dark-300 hover:text-space-blue-400 transition-all duration-300 hover:scale-110">
                  <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
                  </svg>
                </a>
              </div>
            </div>
          </div>

          {/* Right Side - Profile Image with Neon Glow */}
          <div className="flex justify-center lg:justify-end order-1 lg:order-2">
            <div className="relative">
              {/* Outer Glow Ring */}
              <div className="absolute inset-0 rounded-full bg-gradient-to-r from-space-blue-400 via-space-blue-500 to-cosmic-purple-500 animate-pulse opacity-75 blur-lg scale-110 z-0"></div>

              {/* Middle Glow Ring */}
              <div className="absolute inset-2 rounded-full bg-gradient-to-r from-space-blue-300 via-space-blue-400 to-cosmic-purple-400 animate-pulse opacity-50 blur-md scale-105 z-0"></div>

              {/* Inner Glow Ring */}
              <div className="absolute inset-4 rounded-full bg-gradient-to-r from-space-blue-200 via-space-blue-300 to-cosmic-purple-300 animate-pulse opacity-30 blur-sm z-0"></div>

              {/* Profile Circle */}
              <div className="relative w-64 h-64 sm:w-80 sm:h-80 rounded-full shadow-2xl border-4 border-white overflow-hidden z-10">
                {/* Profile Image */}
                <img 
                  src="/profile-photo.png" 
                  alt="Suryansh Srivastava" 
                  className="w-full h-full object-cover rounded-full relative z-20"
                />
              </div>

              {/* Floating Elements around Profile */}
