import React, { useState, useEffect, useCallback, useMemo } from 'react';
import Navbar from './components/Navbar';
import Home from './components/Home';
import About from './components/About';
import Skills from './components/Skills';
import Internships from './components/Internships';
import Certificates from './components/Certificates';
import Contact from './components/Contact';
import Projects from './components/Projects';

// Throttle function for performance optimization
const throttle = (func, limit) => {
  let inThrottle;
  return function() {
    const args = arguments;
    const context = this;
    if (!inThrottle) {
      func.apply(context, args);
      inThrottle = true;
      setTimeout(() => inThrottle = false, limit);
    }
  }
};

function App() {
  const [activeSection, setActiveSection] = useState('home');

  // Memoize sections array to prevent recreation on every render
  const sections = useMemo(() => 
    ['home', 'about', 'projects', 'skills', 'internships', 'certificates', 'contact'], 
    []
  );

  // Optimized scroll handler with throttling
  const handleScroll = useCallback(() => {
    const scrollPosition = window.scrollY + 100;

    for (const section of sections) {
      const element = document.getElementById(section);
      if (element) {
        const { offsetTop, offsetHeight } = element;
        if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
          setActiveSection(section);
          break;
        }
      }
    }
  }, [sections]);

  useEffect(() => {
    // Throttle scroll events to improve performance
    const throttledHandleScroll = throttle(handleScroll, 100);
    
    window.addEventListener('scroll', throttledHandleScroll, { passive: true });
    return () => window.removeEventListener('scroll', throttledHandleScroll);
  }, [handleScroll]);

  const scrollToSection = useCallback((sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-space-dark-950 via-space-dark-900 to-space-blue-950">
      <Navbar activeSection={activeSection} scrollToSection={scrollToSection} />
      <main>
        <Home />
        <About />
        <Projects />
        <Skills />
        <Internships />
        <Certificates />
        <Contact />
      </main>
    </div>
  );
}

export default App;
