import React, { useState, useEffect } from 'react';

const Rocket = ({ activeSection, isMoving }) => {
  const [rocketPosition, setRocketPosition] = useState({ x: 0, y: 0 });
  const [isVisible, setIsVisible] = useState(false);

  // Section positions (approximate positions for rocket movement)
  const sectionPositions = {
    home: { x: 20, y: 20 },
    about: { x: 20, y: 40 },
    skills: { x: 20, y: 60 },
    internships: { x: 20, y: 80 },
    certificates: { x: 20, y: 100 },
    contact: { x: 20, y: 120 }
  };

  useEffect(() => {
    if (activeSection && sectionPositions[activeSection]) {
      setIsVisible(true);
      setRocketPosition(sectionPositions[activeSection]);
    }
  }, [activeSection]);

  return (
    <div className="fixed top-0 left-0 w-full h-full pointer-events-none z-40">
      <div 
        className={`absolute transition-all duration-1000 ease-in-out ${isMoving ? 'animate-rocket-move' : ''}`}
        style={{
          left: `${rocketPosition.x}%`,
          top: `${rocketPosition.y}%`,
          transform: 'translate(-50%, -50%)',
          opacity: isVisible ? 1 : 0
        }}
      >
        {/* Rocket SVG */}
        <div className="relative">
          {/* Rocket Trail */}
          <div className="absolute -bottom-8 left-1/2 transform -translate-x-1/2">
            <div className="w-1 h-16 bg-gradient-to-t from-orange-500 via-yellow-400 to-transparent opacity-80 animate-rocket-trail"></div>
            <div className="absolute -bottom-2 left-1/2 transform -translate-x-1/2 w-2 h-2 bg-orange-400 rounded-full animate-pulse"></div>
          </div>
          
          {/* Main Rocket Body */}
          <svg 
            width="40" 
            height="60" 
            viewBox="0 0 40 60" 
            className="drop-shadow-lg animate-rocket-hover"
          >
            {/* Rocket Body */}
            <ellipse 
              cx="20" 
              cy="45" 
              rx="8" 
              ry="12" 
              fill="#3B82F6" 
              className="animate-rocket-glow"
            />
            
            {/* Rocket Nose */}
            <path 
              d="M20 5 L12 25 L28 25 Z" 
              fill="#60A5FA" 
              className="animate-rocket-glow"
            />
            
            {/* Rocket Fins */}
            <path 
              d="M12 45 L8 55 L12 50 Z" 
              fill="#1E40AF" 
            />
            <path 
              d="M28 45 L32 55 L28 50 Z" 
              fill="#1E40AF" 
            />
            
            {/* Rocket Window */}
            <circle 
              cx="20" 
              cy="35" 
              r="3" 
              fill="#E0F2FE" 
              className="animate-pulse"
            />
            
            {/* Rocket Details */}
            <rect 
              x="18" 
              y="30" 
              width="4" 
              height="2" 
              fill="#1E40AF" 
              rx="1"
            />
            <rect 
              x="18" 
              y="40" 
              width="4" 
              height="2" 
              fill="#1E40AF" 
              rx="1"
            />
          </svg>
          
          {/* Sparkles around rocket */}
          <div className="absolute -top-2 -right-2 w-1 h-1 bg-yellow-300 rounded-full animate-sparkle"></div>
          <div className="absolute -top-1 -left-3 w-1 h-1 bg-blue-300 rounded-full animate-sparkle" style={{animationDelay: '0.5s'}}></div>
          <div className="absolute top-2 -right-4 w-1 h-1 bg-purple-300 rounded-full animate-sparkle" style={{animationDelay: '1s'}}></div>
        </div>
      </div>
    </div>
  );
};

export default Rocket;
